import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api';
import './CookingMode.css';

export default function CookingMode() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [recipe, setRecipe] = useState(null);
  const [currentStep, setCurrentStep] = useState(0);
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const recognitionRef = useRef(null);

  // Parse instructions into steps - FIXED to split by sentences
  const parseInstructions = (instructions) => {
    if (!instructions) return [];

    // If already an array, return it
    if (Array.isArray(instructions)) {
      return instructions.filter(step => step && step.trim() !== '');
    }

    // If it's a string, split it into sentences by period
    if (typeof instructions === 'string') {
      // Split by period (.) to create sentences
      const steps = instructions
        .split('.')
        .map(step => step.trim())
        .filter(step => step.length > 0)
        .map(step => {
          // Remove leading numbers like "1. ", "2) ", "Step 1:", etc.
          return step
            .replace(/^\d+[\.\):\-\s]+/, '')
            .replace(/^Step\s+\d+[\.\):\-\s]*/i, '')
            .trim();
        })
        .filter(step => step.length > 5); // Filter out very short/empty strings

      return steps.length > 0 ? steps : ['No instructions available'];
    }

    return ['No instructions available'];
  };

  // Fetch recipe data
  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const response = await api.get(`/recipes/${id}`);
        const recipeData = response.data;

        // Parse instructions into proper steps
        const parsedInstructions = parseInstructions(recipeData.instructions);

        // Update recipe with parsed instructions
        setRecipe({
          ...recipeData,
          instructions: parsedInstructions.length > 0 ? parsedInstructions : ['No instructions available']
        });
      } catch (error) {
        console.error('Error fetching recipe:', error);
        alert('Failed to load recipe');
        navigate(-1);
      }
    };
    fetchRecipe();
  }, [id, navigate]);

  // Initialize Speech Recognition
  useEffect(() => {
    if (!('webkitSpeechRecognition' in window) && !('SpeechRecognition' in window)) {
      console.warn('Speech recognition not supported');
      return;
    }

    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    const recognition = new SpeechRecognition();

    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.lang = 'en-US';

    recognition.onresult = (event) => {
      let interimTranscript = '';
      let finalTranscript = '';

      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcript = event.results[i][0].transcript.toLowerCase().trim();
        if (event.results[i].isFinal) {
          finalTranscript += transcript;
        } else {
          interimTranscript += transcript;
        }
      }

      const text = (finalTranscript || interimTranscript).toLowerCase();
      setTranscript(text);

      // Voice Commands
      if (text.includes('next') || text.includes('forward')) {
        handleNext();
        setTranscript('');
      } else if (text.includes('back') || text.includes('previous')) {
        handlePrevious();
        setTranscript('');
      } else if (text.includes('exit') || text.includes('quit')) {
        handleExit();
      } else if (text.includes('repeat')) {
        speakInstruction(recipe?.instructions[currentStep]);
        setTranscript('');
      }
    };

    recognition.onerror = (event) => {
      console.error('Speech recognition error:', event.error);
      if (event.error === 'no-speech') {
        // Restart recognition after no speech
        setTimeout(() => {
          if (isListening) {
            recognition.start();
          }
        }, 1000);
      }
    };

    recognition.onend = () => {
      if (isListening) {
        recognition.start(); // Keep listening
      }
    };

    recognitionRef.current = recognition;

    return () => {
      if (recognition) {
        recognition.stop();
      }
    };
  }, [isListening, currentStep, recipe]);

  // Start/Stop listening
  const toggleListening = () => {
    if (isListening) {
      recognitionRef.current?.stop();
      setIsListening(false);
    } else {
      try {
        recognitionRef.current?.start();
        setIsListening(true);
      } catch (error) {
        console.error('Error starting recognition:', error);
      }
    }
  };

  // Text-to-Speech
  const speakInstruction = (instruction) => {
    if ('speechSynthesis' in window) {
      window.speechSynthesis.cancel(); // Cancel any ongoing speech
      const utterance = new SpeechSynthesisUtterance(instruction);
      utterance.rate = 0.9;
      utterance.pitch = 1;
      utterance.volume = 1;
      window.speechSynthesis.speak(utterance);
    }
  };

  const handleNext = () => {
    if (recipe && currentStep < recipe.instructions.length - 1) {
      const newStep = currentStep + 1;
      setCurrentStep(newStep);
      speakInstruction(recipe.instructions[newStep]);
    }
  };

  const handlePrevious = () => {
    if (currentStep > 0) {
      const newStep = currentStep - 1;
      setCurrentStep(newStep);
      speakInstruction(recipe.instructions[newStep]);
    }
  };

  const handleExit = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
    }
    window.speechSynthesis.cancel();
    navigate(-1);
  };

  // Keyboard controls
  useEffect(() => {
    const handleKeyPress = (e) => {
      if (e.key === 'ArrowRight' || e.key === 'n') {
        handleNext();
      } else if (e.key === 'ArrowLeft' || e.key === 'p') {
        handlePrevious();
      } else if (e.key === 'Escape') {
        handleExit();
      } else if (e.key === ' ') {
        e.preventDefault();
        toggleListening();
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [currentStep, recipe, isListening]);

  if (!recipe) {
    return (
      <div className="cooking-mode-loading">
        <div className="loading-spinner"></div>
        <p>Loading recipe...</p>
      </div>
    );
  }

  // Ensure instructions is always an array
  const instructions = Array.isArray(recipe.instructions) 
    ? recipe.instructions 
    : [];

  const totalSteps = instructions.length;
  const progress = totalSteps > 0 ? ((currentStep + 1) / totalSteps) * 100 : 0;

  return (
    <div className="cooking-mode-container">
      {/* Header */}
      <div className="cooking-mode-header">
        <button className="exit-btn" onClick={handleExit} title="Exit (ESC)">
          ‚úï
        </button>
        <h2 className="recipe-title-cooking">{recipe.title}</h2>
        <div className="voice-indicator">
          {isListening && (
            <span className="listening-pulse">
              üé§ Listening...
            </span>
          )}
        </div>
      </div>

      {/* Progress Bar */}
      <div className="progress-container">
        <div className="progress-bar">
          <div 
            className="progress-fill" 
            style={{ width: `${progress}%` }}
          ></div>
        </div>
        <div className="step-counter">
          Step {currentStep + 1} of {totalSteps}
        </div>
      </div>

      {/* Main Instruction */}
      <div className="instruction-container">
        <div className="instruction-number">
          {currentStep + 1}
        </div>
        <div className="instruction-text">
          {instructions[currentStep] || 'No instruction available'}
        </div>
      </div>

      {/* Voice Command Hints */}
      {transcript && (
        <div className="transcript-display">
          Heard: "{transcript}"
        </div>
      )}

      {/* Navigation Controls */}
      <div className="cooking-mode-controls">
        <button 
          className="nav-btn prev-btn"
          onClick={handlePrevious}
          disabled={currentStep === 0}
          title="Previous (‚Üê or 'Back')"
        >
          ‚Üê Previous
        </button>

        <button 
          className={`voice-btn ${isListening ? 'active' : ''}`}
          onClick={toggleListening}
          title="Toggle Voice Control (Space)"
        >
          {isListening ? 'üé§ Stop' : 'üé§ Start Voice'}
        </button>

        <button 
          className="nav-btn next-btn"
          onClick={handleNext}
          disabled={currentStep === totalSteps - 1}
          title="Next (‚Üí or 'Next')"
        >
          Next ‚Üí
        </button>
      </div>

      {/* Voice Commands Help */}
      <div className="voice-commands-help">
        <p>üé§ Voice Commands: "Next" | "Back" | "Repeat" | "Exit"</p>
        <p>‚å®Ô∏è Keyboard: Arrow Keys | Space (voice toggle) | ESC (exit)</p>
      </div>

      {/* Completion Message */}
      {currentStep === totalSteps - 1 && (
        <div className="completion-badge">
          üéâ Final Step!
        </div>
      )}
    </div>
  );
}
