import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import api from '../services/api';

export default function RecipeView() {
  const { id } = useParams();
  const [r, setR] = useState(null);
  useEffect(() => { api.get(`/recipes/${id}`).then(res => setR(res.data)); }, [id]);
  if (!r) return <div>Loading...</div>;
  return (
    <div className="card">
      <h2>{r.title}</h2>
      {r.imageUrl && <img src={`http://localhost:5000${r.imageUrl}`} alt="" />}
      <div><strong>Calories:</strong> {r.caloriesTotal}</div>
      <h4>Ingredients</h4>
      <ul>{r.ingredients.map((ing, i) => <li key={i}>{ing.name} {ing.quantity ? `(${ing.quantity})` : ''} — {ing.calories || '—'} kcal</li>)}</ul>
      <h4>Instructions</h4>
      <p>{r.instructions}</p>
    </div>
  );
}
