import React, { useEffect, useState } from 'react';
import './recipeList.css';
import SearchBar from './SearchBar';
import api from '../services/api';
import { Link } from 'react-router-dom';
import ProfileDrawer from './ProfileDrawer';

export default function PublicFeed() {
  const [recipes, setRecipes] = useState([]);
  const [saved, setSaved] = useState({});
  const [loading, setLoading] = useState({});
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerProfile, setDrawerProfile] = useState(null);

  const base = process.env.REACT_APP_API_URL?.replace('/api','') || 'http://localhost:5000';

  useEffect(() => {
    const load = async () => {
      try {
        const res = await api.get('/recipes/feed');
        setRecipes(res.data || []);
      } catch (err) { console.error('Failed to load public feed', err); }
    };
    load();
  const handler = (e) => { if (e?.detail?.id) openProfile(e.detail.id); };
  window.addEventListener('openProfileDrawer', handler);
  return () => window.removeEventListener('openProfileDrawer', handler);
  }, []);

  const handleSearch = async (q) => {
    try {
      if (!q) {
        const res = await api.get('/recipes/feed');
        setRecipes(res.data || []);
        return;
      }
      const res = await api.get(`/recipes/search?q=${encodeURIComponent(q)}`);
      setRecipes(res.data || []);
    } catch (err) { console.error('search failed', err); }
  };

  const toggleSave = async (recipe) => {
    if (!localStorage.getItem('token')) return alert('Please sign in to save recipes');
    const id = recipe._id;
    setLoading(l => ({ ...l, [id]: true }));
    try {
      const res = await api.post(`/recipes/${id}/favorite`);
      setSaved(s => ({ ...s, [id]: res.data.favorite }));
    } catch (err) { console.error(err); alert('Failed to toggle favorite'); }
    finally { setLoading(l => ({ ...l, [id]: false })); }
  };

  const handleShare = async (recipe) => {
    const url = `${window.location.origin}/recipe/${recipe._id}`;
    if (navigator.share) {
      try { await navigator.share({ title: recipe.title, text: recipe.instructions?.slice(0,120), url }); } catch (e) {}
      return;
    }
    try { await navigator.clipboard.writeText(url); alert('Recipe link copied to clipboard'); } catch (e) { window.prompt('Copy link:', url); }
  };

  const openProfile = async (userId) => {
    try {
      const res = await api.get(`/users/${userId}/public`);
      setDrawerProfile(res.data);
      setDrawerOpen(true);
    } catch (err) { console.error('failed to load profile', err); }
  };

  return (
    <div style={{ padding: 24 }}>
      <h2 style={{ margin: '12px 0 18px', color: '#0f172a' }}>Public Feed</h2>
  <SearchBar onSearch={handleSearch} />
  <div className="rl-grid">
        {recipes.map(r => (
          <article key={r._id} className="rl-card">
            <div className="rl-image" style={{ backgroundImage: `url(${r.imageUrl ? (r.imageUrl.startsWith('http') ? r.imageUrl : base + r.imageUrl) : '/assets/carbonara.jpg'})` }}>
              <div className="rl-cal">{r.caloriesTotal ?? 0} cal</div>
              {r.owner && (
                <div className="rl-author"><img src={r.owner.avatarUrl ? (r.owner.avatarUrl.startsWith('http') ? r.owner.avatarUrl : base + r.owner.avatarUrl) : '/assets/hero.jpg'} alt="author" /> <a href={`/user/${r.owner._id}`} onClick={e => { e.preventDefault(); openProfile(r.owner._id); }}>{r.owner.name}</a></div>
              )}
            </div>

            <div className="rl-body">
              <h3 className="rl-title"><Link to={`/recipe/${r._id}`}>{r.title}</Link></h3>
              {r.owner && <div style={{ fontSize:13, color:'#64748b', marginTop:6 }}>By <Link to={`/user/${r.owner._id}`}>{r.owner.name}</Link></div>}
              <p className="rl-desc">{(r.ingredients||[]).map(i => i.name).slice(0,6).join(', ')}</p>
            </div>

            <div className="rl-footer">
              <span className="rl-date">{new Date(r.createdAt).toLocaleDateString()}</span>
              <div className="rl-actions">
                <button className="icon-btn" title="Share" onClick={() => handleShare(r)}>Share</button>
                <button className="icon-btn" title={saved[r._id] ? 'Remove from favorites' : 'Save'} onClick={() => toggleSave(r)} disabled={loading[r._id]}>
                  {loading[r._id] ? '...' : (saved[r._id] ? 'Saved' : 'Save')}
                </button>
              </div>
            </div>
          </article>
        ))}
      </div>
    <ProfileDrawer open={drawerOpen} profile={drawerProfile} onClose={() => setDrawerOpen(false)} />
    </div>
  );
}
