import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../../services/api';
import './signin.css';

export default function SignIn({ onAuth }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [busy, setBusy] = useState(false);
  const nav = useNavigate();

  const submit = async e => {
    e.preventDefault();
    setBusy(true);
    try {
      const { data } = await api.post('/auth/login', { email, password });
      localStorage.setItem('token', data.token);
      onAuth && onAuth(data.user);
      nav('/dashboard');
    } catch (err) {
      alert(err.response?.data?.message || 'Signin failed');
    } finally {
      setBusy(false);
    }
  };

  return (
    <div className="signin-split">
      <aside className="signin-left">
        <div className="branding">
          <div className="logo-wrap">
            <svg className="chef-hat" viewBox="0 0 24 24" width="46" height="46" fill="none" aria-hidden>
              <path d="M4 12c0-3.3 2.7-6 6-6 .8 0 1.6.2 2.3.6C14.2 6 15 6 16 6c2.8 0 5 2.2 5 5 0 2.3-1.6 4.2-3.8 4.8L16 17H6l-2-5z" stroke="#fff" strokeWidth="0.8" strokeLinecap="round" strokeLinejoin="round"/>
              <path d="M6 17v2a1 1 0 0 0 1 1h10a1 1 0 0 0 1-1v-2" stroke="#fff" strokeWidth="0.8" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
            <div className="brand-text">
              <div className="brand-title">Gemini Kitchen</div>
              <div className="brand-tag">Your AI-powered recipe companion</div>
            </div>
          </div>

          <ul className="features-list">
            <li><span className="dot">ü§ñ</span> AI recipe generation</li>
            <li><span className="dot">üßæ</span> Nutrition analysis</li>
            <li><span className="dot">‚ù§Ô∏è</span> Save your favorites</li>
            <li><span className="dot">üåê</span> Share with community</li>
          </ul>
        </div>
      </aside>

      <main className="signin-right">
        <div className="signin-card">
          <h2>Welcome Back</h2>
          <p className="sub">Sign in to your recipe collection</p>

          <form onSubmit={submit} className="signin-form">
            <label className="field">
              <span className="label-text">Email</span>
              <input
                type="email"
                placeholder="your@email.com"
                value={email}
                onChange={e => setEmail(e.target.value)}
                required
              />
            </label>

            <label className="field">
              <span className="label-text">Password</span>
              <input
                type="password"
                placeholder="Your password"
                value={password}
                onChange={e => setPassword(e.target.value)}
                required
              />
            </label>

            <button type="submit" className="signin-btn" disabled={busy}>
              {busy ? 'Signing in...' : 'Sign In'}
            </button>

            <div className="signup-link">
              Don't have an account? <Link to="/signup">Sign up</Link>
            </div>
          </form>
        </div>
      </main>
    </div>
  );
}
