import React, { useState, useEffect } from 'react';
import { Routes, Route, Link, useNavigate, useLocation } from 'react-router-dom';
import SignIn from './components/Auth/SignIn';
import SignUp from './components/Auth/SignUp';
import Dashboard from './components/Dashboard';
import RecipeView from './components/RecipeView';
import Landing from './components/Landing';
import NewUserDashboard from './components/NewUserDashboard';
import ExistingUserDashboard from './components/ExistingUserDashboard';
import './styles/main.css';

export default function App() {
  const [user, setUser] = useState(() => {
    const raw = localStorage.getItem('user');
    return raw ? JSON.parse(raw) : null;
  });
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    if (user) localStorage.setItem('user', JSON.stringify(user));
    else localStorage.removeItem('user');
  }, [user]);

  const logout = () => {
    setUser(null);
    localStorage.removeItem('token');
    navigate('/signin');
  };

  // Do not show the global topbar on the Landing page (Landing renders its own header)
  const showTopbar = location.pathname !== '/';

  return (
    <div className="app">
      {showTopbar && (
        <header className="topbar">
          <Link to="/" className="logo">CloudRecipes</Link>
          <nav>
            {user ? (
              <>
                <Link to="/dashboard">Dashboard</Link>
                <Link to="/recipes">My Recipes</Link>
                <span className="greet">Hi, {user.name}</span>
                <button onClick={logout}>Logout</button>
              </>
            ) : (
              <>
                <Link to="/signin">Sign In</Link>
                <Link to="/signup" className="signup-btn">Sign Up</Link>
              </>
            )}
          </nav>
        </header>
      )}

      <main>
        <Routes>
          <Route path="/" element={<Landing />} />
          <Route
            path="/dashboard"
            element={
              user && user.name === 'Demo Chef'
                ? <ExistingUserDashboard user={user} />
                : <NewUserDashboard user={user} />
            }
          />
          <Route path="/signin" element={<SignIn onAuth={setUser} />} />
          <Route path="/signup" element={<SignUp onAuth={setUser} />} />
          <Route path="/recipe/:id" element={<RecipeView user={user} />} />
          <Route path="/recipes" element={<Dashboard user={user} />} />
        </Routes>
      </main>
    </div>
  );
}
