import React, { useEffect, useState, useRef } from 'react';
import api from '../services/api';
import './recipeList.css';
import RecipeModal from './RecipeModal';
import RecipeCard from './RecipeCard';
import Toast from './Toast';

export default function PublicFeed() {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [modalRecipe, setModalRecipe] = useState(null);
  const [toast, setToast] = useState({ open: false, message: '', undoId: null });
  const [query, setQuery] = useState('');
  const debounceRef = useRef(null);

  const raw = localStorage.getItem('user');
  const currentUser = raw ? JSON.parse(raw) : null;

  const fetchFeed = async () => {
    setLoading(true);
    try {
      const res = await api.get('/recipes/feed');
      setRecipes(res.data || []);
      setError(null);
    } catch (err) {
      setError(err.response?.data?.message || err.message || 'Failed to load feed');
    } finally {
      setLoading(false);
    }
  };

  const searchRecipes = async (q) => {
    setLoading(true);
    try {
      if (!q) {
        await fetchFeed();
        return;
      }
      const res = await api.get(`/recipes/search?q=${encodeURIComponent(q)}`);
      setRecipes(res.data || []);
      setError(null);
    } catch (err) {
      setError(err.response?.data?.message || err.message || 'Search failed');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchFeed();
  }, []);

  // debounce search
  useEffect(() => {
    if (debounceRef.current) clearTimeout(debounceRef.current);
    debounceRef.current = setTimeout(() => {
      searchRecipes(query.trim());
    }, 350);
    return () => clearTimeout(debounceRef.current);
  }, [query]);

  const handleDelete = async (id) => {
    try {
      await api.delete(`/recipes/${id}`);
      setToast({ open: true, message: 'Recipe deleted', undoId: id });
      // optimistic refresh
      setRecipes(prev => prev.filter(r => r._id !== id));
    } catch (err) {
      console.error(err);
      setError('Delete failed');
    }
  };

  const undoDelete = async () => {
    if (!toast.undoId) return;
    try {
      await api.post(`/recipes/${toast.undoId}/restore`);
      setToast({ open: false, message: '', undoId: null });
      // refresh feed to show restored item
      await fetchFeed();
    } catch (err) {
      console.error(err);
      setError('Restore failed');
    }
  };

  return (
    <div style={{ padding: 28 }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', gap: 12, marginBottom: 16 }}>
        <h2 style={{ margin: 0 }}>Public Feed</h2>
        <div style={{ display: 'flex', gap: 8, alignItems: 'center' }}>
          <input
            value={query}
            onChange={e => setQuery(e.target.value)}
            placeholder="Search recipes by title or ingredient..."
            style={{ padding: '8px 12px', borderRadius: 8, border: '1px solid #e5e7eb', minWidth: 260 }}
          />
        </div>
      </div>

      {loading && <div>Loading...</div>}
      {error && <div className="feed-error">{error}</div>}

      <div className="rl-grid">
        {recipes.map(r => (
          <div key={r._id}>
            <RecipeCard
              recipe={r}
              onChange={() => fetchFeed()}
              onDelete={(() => {
                // allow delete only for owner
                try {
                  const ownerId = r.owner && (r.owner._id || r.owner);
                  if (currentUser && ownerId && currentUser.id && ownerId.toString() === currentUser.id.toString()) {
                    return handleDelete;
                  }
                } catch (e) { /* fallthrough */ }
                return undefined;
              })()}
            />
          </div>
        ))}
      </div>

      <RecipeModal open={!!modalRecipe} recipe={modalRecipe} onClose={() => setModalRecipe(null)} />
      <Toast open={toast.open} message={toast.message} actionText="Undo" onAction={undoDelete} onClose={() => setToast({ open: false })} />
    </div>
  );
}
