import React, { useEffect, useState } from 'react';
import api from '../services/api';
import './recipeList.css';
import carbonara from '../assets/carbonara.jpg';
import salmon from '../assets/salmon.jpg';
import heroImg from '../assets/hero.jpg';
import ProfileDrawer from './ProfileDrawer';

export default function PublicFeed() {
  const [recipes, setRecipes] = useState([]);
  const [saved, setSaved] = useState({});
  const [loading, setLoading] = useState({});
  const [error, setError] = useState(null);
  const [query, setQuery] = useState('');
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerProfile, setDrawerProfile] = useState(null);

  const base = process.env.REACT_APP_API_URL?.replace('/api','') || 'http://localhost:5000';

  const recommended = [
    { id: 'r1', title: 'Creamy Pasta Carbonara', image: carbonara, calories: 520 },
    { id: 'r2', title: 'Herb-Crusted Salmon', image: salmon, calories: 380 },
    { id: 'r3', title: 'Fresh Garden Salad', image: heroImg, calories: 220 },
    { id: 'r4', title: 'Spiced Chickpea Curry', image: heroImg, calories: 430 },
    { id: 'r5', title: 'Avocado Toast Deluxe', image: heroImg, calories: 310 }
  ];

  const fetchFeed = async (q) => {
    setLoading(true);
    setError(null);
    try {
      const url = q ? `/recipes/search?q=${encodeURIComponent(q)}` : '/recipes/feed';
      const res = await api.get(url);
      setRecipes(res.data || []);
    } catch (err) {
      console.error('PublicFeed fetch error:', err);
      setError(err.response?.data?.message || err.message || 'Failed to load feed');
      setRecipes([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchFeed('');
  }, []);

  const onSearch = (e) => {
    e.preventDefault();
    fetchFeed(query);
  };

  const openProfile = (owner) => {
    if (!owner) return;
    const id = owner._id || owner;
    window.dispatchEvent(new CustomEvent('openProfileDrawer', { detail: { id } }));
  };

  return (
    <div style={{ padding: 28 }}>
      <div className="feed-header">
        <h2>Public Feed</h2>
        <form className="feed-search" onSubmit={onSearch}>
          <input value={query} onChange={e => setQuery(e.target.value)} placeholder="Search recipes or username..." />
          <button type="submit">Search</button>
          <button type="button" onClick={() => { setQuery(''); fetchFeed(''); }}>Clear</button>
        </form>
      </div>

      <section className="rec-row" aria-hidden={false}>
        {recommended.map(r => (
          <div key={r.id} className="rec-card" onClick={() => r.id.startsWith('r') ? null : null}>
            <div className="rec-img" style={{ backgroundImage: `url(${r.image})` }} />
            <div className="rec-title">{r.title}</div>
          </div>
        ))}
      </section>

      {error && (
        <div className="feed-error">
          <div>{error}</div>
          <button onClick={() => fetchFeed(query)}>Retry</button>
        </div>
      )}

      {loading ? (
        <div className="rl-grid">Loading...</div>
      ) : (
        <div className="rl-grid wide">
          {recipes.length === 0 ? (
            <div className="empty">No recipes found. Try searching for a dish or user.</div>
          ) : recipes.map(r => (
            <article key={r._id} className="rl-card">
              <div className="rl-image" style={{ backgroundImage: `url(${r.imageUrl ? (r.imageUrl.startsWith('http') ? r.imageUrl : base + r.imageUrl) : '/assets/carbonara.jpg'})` }}>
                <div className="rl-cal">{r.caloriesTotal ?? 0} cal</div>
                <div className="rl-author" onClick={() => openProfile(r.owner)}>
                  <img src={r.owner?.avatarUrl ? (r.owner.avatarUrl.startsWith('http') ? r.owner.avatarUrl : base + r.owner.avatarUrl) : '/assets/avatar-placeholder.png'} alt={r.owner?.name || 'author'} />
                  <span>{r.owner?.name || 'Unknown'}</span>
                </div>
              </div>

              <div className="rl-body">
                <h3 className="rl-title">{r.title}</h3>
                <div className="rl-author-line" onClick={() => openProfile(r.owner)} style={{ cursor: 'pointer', display: 'flex', gap: 8, alignItems: 'center' }}>
                  <img src={r.owner?.avatarUrl ? (r.owner.avatarUrl.startsWith('http') ? r.owner.avatarUrl : (process.env.REACT_APP_API_URL?.replace('/api','') || '') + r.owner.avatarUrl) : '/assets/avatar-placeholder.png'} alt={r.owner?.name || 'author'} style={{ width:28, height:28, borderRadius:999 }} />
                  <strong style={{ fontSize:13 }}>{r.owner?.name || 'Unknown'}</strong>
                </div>
                <p className="rl-desc">{(r.ingredients || []).map(i => i.name).slice(0,6).join(', ')}</p>
              </div>

              <div className="rl-footer">
                <span className="rl-date">{new Date(r.createdAt).toLocaleDateString()}</span>
                <div className="rl-actions">
                  <button className="icon-btn" title="Share" onClick={() => { navigator.clipboard?.writeText(window.location.origin + '/recipe/' + r._id); alert('Link copied'); }}>Share</button>
                  <button className="icon-btn" title="Save">Save</button>
                </div>
              </div>
            </article>
          ))}
        </div>
      )}
    <ProfileDrawer open={drawerOpen} profile={drawerProfile} onClose={() => setDrawerOpen(false)} />
    </div>
  );
}
