import React from 'react';
import './profile.css';

export default function ProfileDrawer({ open, profile, loading, onClose }) {
  if (!open) return null;

  return (
    <div style={{ position: 'fixed', inset: 0, zIndex: 1200 }}>
      <div style={{ position: 'absolute', inset: 0, background: 'rgba(2,6,23,0.45)' }} onClick={onClose} />
  <aside style={{ position: 'absolute', right: 0, top: 0, bottom: 0, width: 420, background: '#fff', boxShadow: ' -24px 0 60px rgba(2,6,23,0.12)', padding: 20, overflowY: 'auto' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <div style={{ display: 'flex', gap: 12, alignItems: 'center' }}>
            <div style={{ width:64, height:64, borderRadius:999, overflow:'hidden', background:'#f1f5f9' }}>
              {loading ? (
                <div style={{ width:64, height:64, background:'#e6e9ee' }} />
              ) : profile?.avatarUrl ? (
                <img src={profile.avatarUrl.startsWith('http') ? profile.avatarUrl : (process.env.REACT_APP_API_URL?.replace('/api','')||'http://localhost:5000') + profile.avatarUrl} alt="avatar" style={{ width:'100%', height:'100%', objectFit:'cover' }} />
              ) : (
                <div style={{ width:64, height:64, display:'flex', alignItems:'center', justifyContent:'center', fontWeight:800 }}>{(profile?.name||'U')[0]}</div>
              )}
            </div>
            <div>
              <div style={{ fontWeight:800, fontSize:18 }}>{loading ? <div style={{ width:120, height:18, background:'#e6e9ee' }} /> : profile?.name}</div>
              <div style={{ color:'#64748b', fontSize:13 }}>{loading ? <div style={{ width:200, height:14, background:'#f1f3f6' }} /> : profile?.bio}</div>
            </div>
          </div>
          <button className="link-like" onClick={onClose}>Close</button>
        </div>

        <div style={{ marginTop:18 }}>
          <h4 style={{ margin: '6px 0' }}>Shared recipes</h4>
          {loading ? (
            <div style={{ display:'grid', gap:12 }}>
              {[1,2,3].map(i => (
                <div key={i} style={{ display:'flex', gap:12, alignItems:'center' }}>
                  <div style={{ width:72, height:56, borderRadius:8, background:'#e9edf4' }} />
                  <div style={{ flex:1 }}>
                    <div style={{ width:160, height:14, background:'#eef2f7', marginBottom:6 }} />
                    <div style={{ width:80, height:12, background:'#f5f7fa' }} />
                  </div>
                </div>
              ))}
            </div>
          ) : (!profile?.recipes || profile.recipes.length === 0) ? (
            <div style={{ color:'#64748b' }}>No public recipes yet.</div>
          ) : (
            <div style={{ display:'grid', gap:12 }}>
              {profile.recipes.map(r => (
                <div key={r._id} style={{ display:'flex', gap:12, alignItems:'center' }}>
                  <div style={{ width:72, height:56, backgroundSize:'cover', backgroundPosition:'center', borderRadius:8, backgroundImage:`url(${r.imageUrl ? (r.imageUrl.startsWith('http') ? r.imageUrl : (process.env.REACT_APP_API_URL?.replace('/api','')||'http://localhost:5000')+r.imageUrl) : '/assets/carbonara.jpg'})` }} />
                  <div>
                    <div style={{ fontWeight:700 }}>{r.title}</div>
                    <div style={{ fontSize:13, color:'#64748b' }}>{new Date(r.createdAt).toLocaleDateString()}</div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </aside>
    </div>
  );
}
