import React, { useState, useMemo } from 'react';

function tokenize(list) {
  return list
    .split(/[,|\n]/g)
    .map(s => s.trim().toLowerCase())
    .filter(Boolean);
}

function titleCase(s) {
  return s.replace(/\b\w/g, c => c.toUpperCase());
}

function generateRecipe(ingList) {
  const ing = tokenize(ingList);
  if (ing.length === 0) {
    return null;
  }

  // Simple rule-based ideas
  const has = (w) => ing.includes(w);
  const any = (...ws) => ws.some(w => ing.includes(w));

  let title = 'Chef’s Quick Creation';
  let cuisine = 'Fusion';
  let description = 'A quick dish created from your available ingredients.';

  if (any('egg', 'eggs') && any('flour', 'pasta', 'noodles')) {
    title = 'Silky Egg Pasta';
    cuisine = 'Italian';
    description = 'Rich, creamy pasta that pairs eggs with pantry staples.';
  } else if (any('rice') && any('egg', 'eggs', 'peas', 'onion')) {
    title = 'Homestyle Egg Fried Rice';
    cuisine = 'Asian';
    description = 'Comforting fried rice with fluffy eggs and crisp veggies.';
  } else if (any('bread') && any('tomato', 'cheese')) {
    title = 'Tomato Cheese Toasties';
    cuisine = 'Café';
    description = 'Golden, melty toasties perfect for a quick bite.';
  } else if (any('potato', 'potatoes') && any('onion', 'garlic')) {
    title = 'Crispy Potato Skillet';
    cuisine = 'Home Kitchen';
    description = 'Crisp, golden potatoes with aromatics — simple and satisfying.';
  } else if (any('chicken') && any('garlic', 'onion')) {
    title = 'Garlic Chicken Stir-Fry';
    cuisine = 'Asian';
    description = 'Fast stir-fry with bold garlic notes and tender chicken.';
  } else if (any('paneer') && any('tomato', 'onion')) {
    title = 'Paneer Masala Quick-Curry';
    cuisine = 'Indian';
    description = 'A quick, cozy masala with paneer and everyday spices.';
  } else if (any('egg', 'eggs') && any('bread', 'onion', 'tomato')) {
    title = 'Masala Egg Bread Scramble';
    cuisine = 'Indian Street';
    description = 'Tava-style masala eggs folded with toasted bread.';
  }

  // Ingredients: use user items plus minimal pantry boosts
  const pantry = ['salt', 'black pepper', 'oil'];
  const ingredients = Array.from(new Set([...ing, ...pantry])).map(titleCase);

  // Steps template
  const steps = [
    'Prep all ingredients (wash, peel, chop as needed).',
    'Heat a pan with a little oil on medium flame.',
    'Add aromatics first (like onion/garlic) and sauté till fragrant.',
    'Add main ingredients and cook until done, stirring occasionally.',
    'Season with salt and pepper; adjust to your taste.',
    'Finish with any herbs/chili/lemon and serve warm.'
  ];

  // Rough time/servings
  const time = Math.min(35, 10 + ing.length * 3);
  const servings = ing.length >= 6 ? 3 : 2;

  return {
    title,
    cuisine,
    description,
    ingredients,
    steps,
    time,
    servings,
  };
}

export default function AIGenerateModal({ open, onClose }) {
  const [raw, setRaw] = useState('egg, tomato, onion, bread');
  const recipe = useMemo(() => generateRecipe(raw), [raw]);

  if (!open) return null;

  return (
    <div
      role="dialog"
      aria-modal="true"
      style={{
        position: 'fixed', inset: 0, background: 'rgba(0,0,0,0.45)',
        zIndex: 1000, display: 'flex', alignItems: 'center', justifyContent: 'center'
      }}
      onClick={(e) => { if (e.target === e.currentTarget) onClose?.(); }}
    >
      <div style={{
        width: 'min(720px, 92vw)', maxHeight: '88vh', overflow: 'auto',
        background: '#fff', borderRadius: 12, boxShadow: '0 12px 40px rgba(0,0,0,0.25)',
        padding: 20
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', gap: 12 }}>
          <h3 style={{ margin: 0 }}>Generate Recipe from Ingredients</h3>
          <button onClick={onClose} className="btn" style={{ padding: '6px 10px' }}>✕</button>
        </div>

        <label style={{ display: 'block', marginTop: 12, fontWeight: 600 }}>Your ingredients (comma or new line):</label>
        <textarea
          value={raw}
          onChange={e => setRaw(e.target.value)}
          placeholder="e.g. egg, bread, tomato, onion"
          rows={4}
          style={{
            width: '100%', borderRadius: 8, border: '1px solid #d4d7dd',
            padding: 10, fontSize: 14, marginTop: 6
          }}
        />

        <div style={{ marginTop: 16 }}>
          <button
            className="btn btn-primary"
            onClick={() => setRaw(r => r.trim())}
            style={{ padding: '10px 14px' }}
          >
            Generate
          </button>
        </div>

        {recipe && (
          <div style={{ marginTop: 18, borderTop: '1px solid #eee', paddingTop: 14 }}>
            <h4 style={{ margin: '0 0 6px' }}>{recipe.title} <span style={{ color: '#6b7280' }}>· {recipe.cuisine}</span></h4>
            <p style={{ marginTop: 0, color: '#475569' }}>{recipe.description}</p>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 14 }}>
              <div>
                <div style={{ fontWeight: 600, marginBottom: 6 }}>Ingredients</div>
                <ul style={{ margin: 0, paddingLeft: 18 }}>
                  {recipe.ingredients.map((it, i) => <li key={i}>{it}</li>)}
                </ul>
              </div>
              <div>
                <div style={{ fontWeight: 600, marginBottom: 6 }}>Steps</div>
                <ol style={{ margin: 0, paddingLeft: 18 }}>
                  {recipe.steps.map((s, i) => <li key={i} style={{ marginBottom: 6 }}>{s}</li>)}
                </ol>
              </div>
            </div>
            <div style={{ marginTop: 10, color: '#64748b' }}>
              Ready in ~{recipe.time} mins · Serves {recipe.servings}
            </div>

            <div style={{ marginTop: 14, display: 'flex', gap: 10, flexWrap: 'wrap' }}>
              <button
                className="btn btn-cta"
                onClick={() => {
                  const blob = new Blob([JSON.stringify(recipe, null, 2)], { type: 'application/json' });
                  const url = URL.createObjectURL(blob);
                  const a = document.createElement('a');
                  a.href = url; a.download = `${recipe.title.replace(/\s+/g, '_')}.json`; a.click();
                  URL.revokeObjectURL(url);
                }}
              >
                Download JSON
              </button>
              <button
                className="btn btn-ghost"
                onClick={async () => {
                  try {
                    await navigator.clipboard.writeText(
                      `${recipe.title}\n\nIngredients:\n- ${recipe.ingredients.join('\n- ')}\n\nSteps:\n${recipe.steps.map((s,i)=>`${i+1}. ${s}`).join('\n')}`
                    );
                    alert('Copied to clipboard');
                  } catch {
                    alert('Copy failed');
                  }
                }}
              >
                Copy to Clipboard
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
