          ) : (
            <div className="recipe-grid">
              {feedPreview.slice(0, 4).map(r => (
                <div key={r._id || r.id} style={{ background: 'white', borderRadius: '16px', overflow: 'hidden', boxShadow: '0 6px 24px rgba(0,0,0,0.2)' }}>
