import React, { useEffect, useState } from 'react';
import api from '../services/api';
import './recipeList.css';
import RecipeModal from './RecipeModal';
import carbonara from '../assets/carbonara.jpg';
import ProfileDrawer from './ProfileDrawer';
import estimateCalories from '../services/calorieEstimator';
import Toast from './Toast';

export default function PublicFeed() {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [query, setQuery] = useState('');
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerProfile, setDrawerProfile] = useState(null);
  const [modalRecipe, setModalRecipe] = useState(null);
  const [toast, setToast] = useState({ open: false, message: '', undoId: null });

  const base = process.env.REACT_APP_API_URL?.replace('/api', '') || 'http://localhost:5000';

  const fetchFeed = async (q) => {
    setLoading(true);
    setError(null);
    try {
      const url = q ? `/recipes/search?q=${encodeURIComponent(q)}` : '/recipes/feed';
      const res = await api.get(url);
      setRecipes(res.data || []);
    } catch (err) {
      console.error('PublicFeed fetch error:', err);
      setError(err.response?.data?.message || err.message || 'Failed to load feed');
      setRecipes([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchFeed('');
  }, []);

  const onSearch = (e) => {
    e.preventDefault();
    fetchFeed(query);
  };

  const openProfile = (owner) => {
    if (!owner) return;
    const id = owner._id || owner;
    window.dispatchEvent(new CustomEvent('openProfileDrawer', { detail: { id } }));
  };

  const openRecipeModal = (r) => setModalRecipe(r);
  const closeModal = () => setModalRecipe(null);

  const handleDelete = async (id) => {
    try {
      await api.delete(`/recipes/${id}`);
      setToast({ open: true, message: 'Recipe deleted', undoId: id });
      fetchFeed(query);
    } catch (err) {
      console.error('delete failed', err);
      alert('Delete failed');
    }
  };

  const undoDelete = async () => {
    try {
      if (!toast.undoId) return;
      await api.post(`/recipes/${toast.undoId}/restore`);
      setToast({ open: false, message: '', undoId: null });
      fetchFeed(query);
    } catch (err) {
      console.error('undo failed', err);
    }
  };

  return (
    <div style={{ padding: 28 }}>
      <div className="feed-header">
        <h2><span className="feed-icon" aria-hidden>üçΩ</span> Public Feed</h2>
        <div style={{ display: 'flex', gap: 8, alignItems: 'center' }} />
        <form className="feed-search" onSubmit={onSearch}>
          <input value={query} onChange={e => setQuery(e.target.value)} placeholder="Search recipes or username..." />
          <button type="submit" className="search-btn">Search</button>
          <button type="button" className="clear-btn" onClick={() => { setQuery(''); fetchFeed(''); }}>Clear</button>
        </form>
      </div>

      {error && (
        <div className="feed-error">
          <div>{error}</div>
          <button onClick={() => fetchFeed(query)}>Retry</button>
        </div>
      )}

      {loading ? (
        <div className="rl-grid">Loading...</div>
      ) : (
        <div className="rl-grid wide">
          {recipes.length === 0 ? (
            <div className="empty">No recipes found. Try searching for a dish or user.</div>
          ) : recipes.map(r => (
            <article key={r._id} className="rl-card" onClick={() => openRecipeModal(r)} style={{ cursor: 'pointer' }}>
              <div className="rl-image" style={{ backgroundImage: `url(${r.imageUrl ? (r.imageUrl.startsWith('http') ? r.imageUrl : base + r.imageUrl) : carbonara})` }}>
                <div className="rl-cal">{(r.caloriesTotal && r.caloriesTotal > 0 ? r.caloriesTotal : estimateCalories(r))} cal</div>
                {r.owner && r.owner.name ? (
                  <div className="rl-author" onClick={(e) => { e.stopPropagation(); openProfile(r.owner); }}>
                    <img src={r.owner?.avatarUrl ? (r.owner.avatarUrl.startsWith("http") ? r.owner.avatarUrl : base + r.owner.avatarUrl) : '/assets/avatar-placeholder.png'} alt={r.owner?.name || 'author'} />
                    <span>{r.owner?.name}</span>
                  </div>
                ) : null}
                {r.owner && (() => {
                  try {
                    const stored = localStorage.getItem('user');
                    if (!stored) return null;
                    const me = JSON.parse(stored);
                    const ownerId = r.owner._id || r.owner;
                    // support both _id and id fields
                    const meId = me._id || me.id || me.id;
                    if (meId && ownerId && meId.toString() === ownerId.toString()) {
                      return (
                        <div style={{ position: 'absolute', right: 12, top: 12 }} onClick={(e) => { e.stopPropagation(); if (window.confirm('Delete this recipe?')) handleDelete(r._id); }}>
                          <button style={{ background: 'transparent', border: 'none', color: '#ff4d4f', cursor: 'pointer' }}>üóë</button>
                        </div>
                      );
                    }
                  } catch (err) {
                    return null;
                  }
                  return null;
                })()}
              </div>

              <div className="rl-body">
                <h3 className="rl-title">{r.title}</h3>
                <p className="rl-desc">{r.summary || ((r.ingredients || []).slice(0,4).map(i=>i.name).join(', '))}</p>
              </div>

              <div className="rl-footer">
                <span className="rl-date">{new Date(r.createdAt).toLocaleDateString()}</span>
              </div>
            </article>
          ))}
        </div>
      )}

      <RecipeModal open={!!modalRecipe} recipe={modalRecipe} onClose={closeModal} />
      <ProfileDrawer open={drawerOpen} profile={drawerProfile} onClose={() => setDrawerOpen(false)} />
      <Toast open={toast.open} message={toast.message} actionText="Undo" onAction={undoDelete} onClose={() => setToast({ open: false })} />
    </div>
  );
}
