import React, { useEffect, useState } from 'react';
import estimateCalories from '../services/calorieEstimator';
import { Link } from 'react-router-dom';
import api from '../services/api';

export default function RecipeCard({ recipe, onChange, onDelete, onOpen }) {
  const base = process.env.REACT_APP_API_URL?.replace('/api','') || 'http://localhost:5000';

  const [liked, setLiked] = useState(false);
  const [likes, setLikes] = useState(recipe.likes || 0);

  useEffect(() => {
    try {
      const raw = localStorage.getItem('user');
      if (!raw) return setLiked(false);
      const me = JSON.parse(raw);
      const favs = me && (me.favorites || []);
      setLiked(Array.isArray(favs) && favs.findIndex(f => f && f.toString() === (recipe._id || '').toString()) !== -1);
    } catch (err) {
      setLiked(false);
    }
  }, [recipe._id]);

  const isOwner = (() => {
    try {
      const raw = localStorage.getItem('user');
      if (!raw) return false;
      const me = JSON.parse(raw);
      const meId = me._id || me.id || me;
      const ownerId = recipe.owner && (recipe.owner._id ? recipe.owner._id : recipe.owner);
      return !!(meId && ownerId && meId.toString() === ownerId.toString());
    } catch (err) { return false; }
  })();

  const toggleFav = async (e) => {
    e && e.stopPropagation();
    try {
      const next = !liked;
      setLiked(next);
      setLikes(l => next ? l + 1 : Math.max(0, l - 1));

      const res = await api.post(`/recipes/${recipe._id}/favorite`);
      try {
        const raw = localStorage.getItem('user');
        if (raw) {
          const me = JSON.parse(raw);
          if (res && res.favorites) me.favorites = res.favorites;
          else {
            me.favorites = me.favorites || [];
            const idx = me.favorites.findIndex(f => f && f.toString() === recipe._id.toString());
            if (next && idx === -1) me.favorites.push(recipe._id);
            if (!next && idx !== -1) me.favorites.splice(idx, 1);
          }
          localStorage.setItem('user', JSON.stringify(me));
        }
      } catch (err) { }

      onChange && onChange();
    } catch (err) {
      console.error('favorite failed', err);
      setLiked(prev => !prev);
      setLikes(l => Math.max(0, l + (liked ? 1 : -1)));
      if (err.response && err.response.status === 401) alert('Please sign in to favorite recipes');
    }
  };

  const openProfile = (id, e) => {
    e && e.stopPropagation();
    window.dispatchEvent(new CustomEvent('openProfileDrawer', { detail: { id } }));
  };

  const handleOpen = (e) => {
    if (onOpen) onOpen(recipe);
  };

  // short description fallback
  const shortDesc = recipe.summary || ((recipe.ingredients || []).slice(0,4).map(i=>i.name).join(', '));

  return (
    <div className="recipe-card card" onClick={handleOpen} style={{ cursor: onOpen ? 'pointer' : 'default' }}>
      <div className="rc-image" style={{ backgroundImage: `url(${recipe.imageUrl ? (recipe.imageUrl.startsWith('http') ? recipe.imageUrl : base + recipe.imageUrl) : ''})` }}>
        <div className="rc-cal">{(recipe.caloriesTotal && recipe.caloriesTotal > 0 ? recipe.caloriesTotal : estimateCalories(recipe))} cal</div>
        {recipe.owner && recipe.owner.name ? (
          <div className="rc-author" onClick={(e) => openProfile(recipe.owner._id || recipe.owner, e)}>
            <img src={recipe.owner?.avatarUrl ? (recipe.owner.avatarUrl.startsWith("http") ? recipe.owner.avatarUrl : base + recipe.owner.avatarUrl) : '/assets/avatar-placeholder.png'} alt={recipe.owner?.name || 'author'} />
            <span>{recipe.owner?.name}</span>
          </div>
        ) : null}
      </div>

      <div className="meta">
        <Link to={`/recipe/${recipe._id}`} className="title" onClick={(e)=>{ e.stopPropagation(); }}>{recipe.title}</Link>
        <div className="rl-desc">{shortDesc}</div>
        <div className="cal">{(recipe.caloriesTotal && recipe.caloriesTotal > 0 ? recipe.caloriesTotal : estimateCalories(recipe))} kcal</div>
      </div>

      <div className="actions">
        <button onClick={toggleFav} title="Favorite" style={{ background: 'transparent', border: 'none', cursor: 'pointer', fontSize: 16 }}>
          {liked ? 'â¤ï¸' : 'ðŸ¤'} {likes > 0 ? likes : ''}
        </button>
        {onDelete && isOwner && (
          <button onClick={(e) => { e.stopPropagation(); if (window.confirm('Delete this recipe? This cannot be undone.')) { onDelete(recipe._id); } }} title="Delete" style={{ marginLeft: 8 }}>ðŸ—‘</button>
        )}
      </div>
    </div>
  );
}
