import React from 'react';
import { Link } from 'react-router-dom';
import carbonara from '../assets/carbonara.jpg';
import salmon from '../assets/salmon.jpg';
import heroImg from '../assets/hero.jpg';
import bgBehind from '../assets/clom.jpg';
import Features from './Features';

const headingWords = ['Your', 'Personal', 'Organic', 'Cloud', 'Kitchen'];

export default function Landing() {
  return (
    <div className="relative w-full overflow-hidden bg-white text-gray-900">
      {/* Soft Aurora / Radial Gradient Background */}
      <div className="pointer-events-none absolute inset-0 -z-10">
        <div className="absolute left-1/2 top-10 h-[700px] w-[700px] -translate-x-1/2 rounded-full bg-[radial-gradient(circle_at_center,_rgba(255,122,45,0.18),_transparent_70%)]" />
        <div className="absolute -bottom-32 -right-32 h-[600px] w-[600px] rounded-full bg-[radial-gradient(circle_at_center,_rgba(244,140,6,0.15),_transparent_70%)]" />
        <div className="absolute -top-40 -left-40 h-[500px] w-[500px] rounded-full bg-[radial-gradient(circle_at_center,_rgba(234,88,12,0.10),_transparent_70%)]" />
      </div>

      {/* Nav */}
      <header className="flex items-center justify-between max-w-7xl mx-auto px-8 pt-6 pb-4 sticky top-0 z-50 bg-white/70 backdrop-blur-md border-b border-white/40 rounded-b-xl">
        <div className="flex items-center gap-2 font-extrabold text-xl">
          <span className="inline-block w-8 h-8 rounded-full bg-gradient-to-tr from-orange-500 to-amber-400 shadow-lg" />
          HomePlate
        </div>
        <nav className="flex items-center gap-3">
          <Link className="px-4 py-2 rounded-full text-sm font-medium bg-gray-100 hover:bg-gray-200 transition-colors" to="/signin">Login</Link>
          <Link className="px-4 py-2 rounded-full text-sm font-semibold bg-gradient-to-r from-orange-500 to-amber-500 text-white shadow hover:shadow-md transition-all" to="/signup">Sign Up</Link>
        </nav>
      </header>

      {/* HERO SECTION */}
      <section className="relative max-w-7xl mx-auto px-8 pt-14 pb-20 grid lg:grid-cols-2 gap-14">
        <div className="flex flex-col justify-center">
          <div className="inline-flex items-center gap-2 px-4 py-2 rounded-full bg-white/50 backdrop-blur-md border border-white/40 text-sm font-medium text-orange-600 shadow-sm mb-6 fade-in">
            ðŸŒ¿ Modern Organic Platform
          </div>
          <h1 className="font-playfair font-black leading-[1.05] text-5xl sm:text-6xl xl:text-7xl tracking-tight mb-8">
            {headingWords.map((w, i) => (
              <span
                key={w}
                style={{
                  display: 'inline-block',
                  marginRight: '0.75rem',
                  opacity: 0,
                  animation: 'rise 0.6s ease forwards',
                  animationDelay: `${i * 0.12}s`
                }}
              >
                {w}
              </span>
            ))}
            <span
              style={{
                backgroundImage: 'linear-gradient(to right, #f97316, #fbbf24, #f97316)',
                WebkitBackgroundClip: 'text',
                color: 'transparent',
                display: 'inline-block',
                animation: 'gradientReveal 1.2s ease forwards',
                animationDelay: '0.4s'
              }}
            >
              Experience
            </span>
          </h1>
          <p className="text-lg leading-relaxed max-w-xl text-gray-700 mb-8 fade-up" style={{ animationDelay: '0.6s' }}>
            Organize, generate, and share recipes enhanced by AI nutrition insights. A premium cooking workspace blending technology, creativity, and flavor.
          </p>
          <div className="flex flex-wrap gap-4 fade-up" style={{ animationDelay: '0.8s' }}>
            <Link to="/signup" className="px-7 py-4 rounded-xl bg-gradient-to-r from-orange-500 to-amber-500 text-white font-semibold text-base shadow-lg shadow-orange-500/30 hover:shadow-xl hover:-translate-y-0.5 transition-all">Start Cooking â†’</Link>
            <Link to="/signin" className="px-7 py-4 rounded-xl bg-white/60 backdrop-blur border border-gray-200 text-gray-800 font-medium hover:bg-white transition">Sign In</Link>
          </div>
          <div className="mt-10 grid grid-cols-3 gap-6 max-w-md fade-in" style={{ animationDelay: '1s' }}>
            {[
              { n: '10K+', l: 'Recipes' },
              { n: '5K+', l: 'Users' },
              { n: '4.9â˜…', l: 'Rating' }
            ].map(s => (
              <div key={s.l} className="text-center p-4 rounded-xl bg-white/50 backdrop-blur border border-white/40 shadow-sm">
                <div className="text-2xl font-bold bg-gradient-to-r from-orange-500 to-amber-500 bg-clip-text text-transparent">{s.n}</div>
                <div className="text-xs uppercase tracking-wide text-gray-600">{s.l}</div>
              </div>
            ))}
          </div>
        </div>

        {/* Floating Visual Cards */}
        <div className="relative h-full">
          <div className="absolute inset-0 rounded-3xl bg-gradient-to-tr from-orange-100/30 to-amber-100/30 blur-2xl" />
          <div className="relative w-full h-full flex items-center justify-center">
            <div className="w-72 h-80 rounded-3xl overflow-hidden shadow-xl shadow-orange-500/30 border border-white/50 backdrop-blur bg-white/40 float-slow">
              <img src={heroImg} alt="Showcase" className="w-full h-full object-cover" />
            </div>
            <div className="absolute -right-6 top-10 w-44 h-56 rounded-2xl overflow-hidden shadow-lg border border-white/50 backdrop-blur bg-white/40 float-medium">
              <img src={salmon} alt="Salmon" className="w-full h-full object-cover" />
            </div>
            <div className="absolute -left-6 bottom-8 w-40 h-52 rounded-2xl overflow-hidden shadow-lg border border-white/50 backdrop-blur bg-white/40 float-fast">
              <img src={carbonara} alt="Carbonara" className="w-full h-full object-cover" />
            </div>
          </div>
        </div>
      </section>

      {/* MARQUEE STRIP */}
      <div className="relative w-full overflow-hidden bg-gray-900 py-4">
        <div className="animate-marquee whitespace-nowrap text-white text-lg font-medium">
          {Array.from({ length: 30 }).map((_, i) => (
            <span key={i} className="mx-6">Fresh â€¢ Organic â€¢ AI-Powered â€¢ Tasty â€¢</span>
          ))}
        </div>
      </div>

      {/* BENTO FEATURE GRID */}
      <section className="max-w-7xl mx-auto px-8 py-20">
        <h2 className="font-playfair font-black text-4xl sm:text-5xl mb-14 text-center fade-up" style={{ animationDelay: '0.2s' }}>
          Elevate Your Culinary Workflow
        </h2>
        <div className="grid grid-cols-6 auto-rows-[220px] gap-6">
          <div className="col-span-6 lg:col-span-3 row-span-2 rounded-3xl p-8 flex flex-col justify-between bg-white/30 backdrop-blur-xl border border-white/50 shadow-xl fade-up" style={{ animationDelay: '0.3s' }}>
            <div>
              <h3 className="text-2xl font-bold mb-3">AI Recipe Generation</h3>
              <p className="text-sm leading-relaxed text-gray-700">Input ingredients and instantly receive chef-quality, nutritionally analyzed recipes tailored to your preferences.</p>
            </div>
            <div className="mt-6 flex flex-wrap gap-2 text-xs">
              {['Speed','Customization','Nutrition','Zero Waste'].map(t => <span key={t} className="px-3 py-1 rounded-full bg-white/60 backdrop-blur border border-white/40 text-gray-700">{t}</span>)}
            </div>
          </div>
          <div className="col-span-6 sm:col-span-3 lg:col-span-2 rounded-3xl p-6 flex flex-col bg-white/30 backdrop-blur-xl border border-white/50 shadow-xl fade-up" style={{ animationDelay: '0.4s' }}>
            <h3 className="text-xl font-semibold mb-3">Smart Organization</h3>
            <p className="text-sm text-gray-700 mb-4">Tag, categorize, and version recipes while maintaining ingredient histories.</p>
            <div className="mt-auto text-xs flex flex-wrap gap-2">
              {['Tags','Search','History'].map(t => <span key={t} className="px-3 py-1 rounded-full bg-white/60 border border-white/40 text-gray-700">{t}</span>)}
            </div>
          </div>
          <div className="col-span-6 sm:col-span-3 lg:col-span-1 rounded-3xl p-6 flex flex-col items-start justify-between bg-white/30 backdrop-blur-xl border border-white/50 shadow-xl fade-up" style={{ animationDelay: '0.5s' }}>
            <div>
              <h3 className="text-lg font-semibold mb-2">Nutrition</h3>
              <p className="text-xs text-gray-700">Auto macro + calorie stats with health flags.</p>
            </div>
            <div className="text-xs flex flex-col gap-1 mt-3">
              <span className="px-2 py-1 rounded bg-gradient-to-r from-orange-500 to-amber-500 text-white">Macros</span>
              <span className="px-2 py-1 rounded bg-white/70 backdrop-blur border border-white/40">Allergens</span>
            </div>
          </div>
          <div className="col-span-6 sm:col-span-3 lg:col-span-2 rounded-3xl p-6 flex flex-col bg-white/30 backdrop-blur-xl border border-white/50 shadow-xl fade-up" style={{ animationDelay: '0.6s' }}>
            <h3 className="text-xl font-semibold mb-3">Community Discovery</h3>
            <p className="text-sm text-gray-700 mb-4">Follow creative cooks and explore global culinary trends.</p>
            <div className="mt-auto flex -space-x-2">
              {['ðŸ‘©â€ðŸ³','ðŸ‘¨â€ðŸ³','ðŸ§‘â€ðŸ³','ðŸ³'].map(a => <span key={a} className="w-9 h-9 rounded-full bg-white/70 backdrop-blur border border-white/40 flex items-center justify-center text-lg shadow">{a}</span>)}
            </div>
          </div>
        </div>
      </section>

      {/* TESTIMONIALS */}
      <section className="max-w-7xl mx-auto px-8 py-20">
        <h2 className="font-playfair font-black text-4xl sm:text-5xl mb-12 text-center fade-up" style={{ animationDelay: '0.2s' }}>Loved by Food Innovators</h2>
        <div className="grid md:grid-cols-3 gap-8">
          {[
            { q: 'The AI generator revolutionized my weekday cooking!', a: 'Sarah Johnson', r: 'Home Chef' },
            { q: 'Nutrition insights keep me balanced while experimenting.', a: 'Michael Chen', r: 'Food Blogger' },
            { q: 'Community recipes inspire endless creativity.', a: 'Emily Rodriguez', r: 'Culinary Student' }
          ].map((t, i) => (
            <div key={t.a} className="p-8 rounded-3xl bg-white/40 backdrop-blur border border-white/50 shadow-xl fade-up" style={{ animationDelay: `${0.3 + i * 0.15}s` }}>
              <div className="text-5xl leading-none mb-4 text-orange-500 font-serif">â€œ</div>
              <p className="text-sm text-gray-700 mb-6">{t.q}</p>
              <div className="flex items-center gap-3">
                <span className="w-12 h-12 rounded-full bg-gradient-to-tr from-orange-500 to-amber-400 flex items-center justify-center text-xl">{t.a[0]}</span>
                <div>
                  <div className="font-semibold text-sm">{t.a}</div>
                  <div className="text-xs text-gray-500">{t.r}</div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* CTA */}
      <section className="relative max-w-5xl mx-auto px-8 py-20">
        <div className="rounded-3xl p-12 bg-gradient-to-br from-orange-500 via-amber-500 to-orange-600 text-white shadow-2xl fade-in">
          <h2 className="font-playfair font-black text-4xl sm:text-5xl mb-6">Ready to Start Your Culinary Journey?</h2>
          <p className="text-lg text-orange-50/90 mb-8 max-w-2xl">Join thousands of creators leveraging AI to enhance flavor, health, and creativity. Your smart kitchen awaits.</p>
          <div className="flex flex-wrap gap-4">
            <Link to="/signup" className="px-8 py-4 rounded-xl bg-white text-orange-600 font-semibold shadow hover:shadow-lg transition">Get Started Free</Link>
            <Link to="/signin" className="px-8 py-4 rounded-xl bg-white/20 backdrop-blur border border-white/40 font-semibold hover:bg-white/30 transition">Sign In</Link>
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="border-t border-gray-200 py-10 text-center text-sm text-gray-600">Â© 2024 HomePlate. All rights reserved.</footer>

      {/* Animations & Marquee keyframes */}
      <style>{`
        .animate-marquee {animation: marquee 40s linear infinite;}
        @keyframes marquee {0% {transform: translateX(0);}100% {transform: translateX(-50%);} }
        .fade-in {opacity:0;animation: fadeIn 0.9s ease forwards;}
        .fade-up {opacity:0;transform:translateY(30px);animation: fadeUp 0.9s ease forwards;}
        @keyframes fadeIn {to {opacity:1;}}
        @keyframes fadeUp {to {opacity:1;transform:translateY(0);}}
        @keyframes rise {0% {opacity:0;transform:translateY(40px);}100% {opacity:1;transform:translateY(0);} }
        @keyframes gradientReveal {0% {background-size:0% 100%;}100% {background-size:100% 100%;}}
        .float-slow {animation: float1 6s ease-in-out infinite alternate;}
        .float-medium {animation: float2 7s ease-in-out infinite alternate;}
        .float-fast {animation: float3 5s ease-in-out infinite alternate;}
        @keyframes float1 {0% {transform:translateY(0);}100% {transform:translateY(-25px);} }
        @keyframes float2 {0% {transform:translateY(0);}100% {transform:translateY(-35px);} }
        @keyframes float3 {0% {transform:translateY(0);}100% {transform:translateY(-20px);} }
      `}</style>
    </div>
  );
}
