import React, { useMemo, useState } from 'react';
import api from '../services/api';

function tokenize(list) {
  return list
    .split(/[\s,;|\n/]+/g) // split on spaces, commas, semicolons, pipes, newlines, slashes
    .map(s => s.trim().toLowerCase())
    .filter(Boolean);
}

function titleCase(s) { return s.replace(/\b\w/g, c => c.toUpperCase()); }

function localGenerate(ingList) {
  const ing = tokenize(ingList);
  if (!ing.length) return null;
  const hasAny = (...w) => w.some(x => ing.includes(x));
  let title = 'Chef’s Quick Creation', cuisine = 'Fusion', description = 'A quick dish from your ingredients.';
  if (hasAny('rice') && hasAny('egg','eggs','peas','onion')) { title='Homestyle Egg Fried Rice'; cuisine='Asian'; description='Comforting fried rice.'; }
  else if (hasAny('bread') && hasAny('tomato','cheese')) { title='Tomato Cheese Toasties'; cuisine='Café'; description='Golden, melty toasties.'; }
  else if (hasAny('paneer') && hasAny('tomato','onion')) { title='Paneer Masala Quick-Curry'; cuisine='Indian'; description='Cozy paneer masala.'; }
  const ingredients = Array.from(new Set([...ing, 'salt','black pepper','oil'])).map(titleCase);
  const steps = [
    'Prep ingredients (wash, chop).',
    'Heat a pan with a little oil.',
    'Sauté aromatics until fragrant.',
    'Add mains and cook through.',
    'Season and finish with herbs/chili/lemon.'
  ];
  return { title, cuisine, description, ingredients, steps, time: Math.min(35, 10 + ing.length*3), servings: ing.length>=6?3:2 };
}

export default function AIGenerateModal({ open, onClose }) {
  const [raw, setRaw] = useState('egg, tomato, onion, bread');
  const [remote, setRemote] = useState(null);
  const [loading, setLoading] = useState(false);
  const [status, setStatus] = useState('');
  const localRecipe = useMemo(() => localGenerate(raw), [raw]);
  const recipe = remote || localRecipe;

  if (!open) return null;

  const handleGenerate = async () => {
    setLoading(true);
    setRemote(null);
    setStatus('');
    try {
      const res = await api.post('/ai/generate', { ingredients: raw });
      setRemote(res.data);
      setStatus('AI result');
    } catch (e) {
      console.warn('AI endpoint unavailable, using local generator');
      setStatus('Local suggestion');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      role="dialog"
      aria-modal="true"
      onClick={(e)=>{ if(e.target===e.currentTarget) onClose?.(); }}
      style={{
        position: 'fixed', inset: 0, background: 'rgba(0,0,0,0.45)',
        zIndex: 1000, display: 'flex', alignItems: 'center', justifyContent: 'center'
      }}
    >
      <div style={{
        width: 'min(720px, 92vw)', maxHeight: '88vh', overflow: 'auto',
        background: '#fff', borderRadius: 12, boxShadow: '0 12px 40px rgba(0,0,0,0.25)',
        padding: 20
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', gap: 12 }}>
          <h3 style={{ margin: 0 }}>Generate Recipe from Ingredients</h3>
          <button onClick={onClose} className="btn" style={{ padding: '6px 10px' }}>✕</button>
        </div>

        <label style={{ display: 'block', marginTop: 12, fontWeight: 600 }}>Your ingredients (comma or new line):</label>
        <textarea
          value={raw}
          onChange={e => setRaw(e.target.value)}
          rows={4}
          placeholder="e.g. egg bread tomato onion OR egg, bread, tomato, onion"
          style={{
            width: '100%', borderRadius: 8, border: '1px solid #d4d7dd',
            padding: 10, fontSize: 14, marginTop: 6
          }}
        />
        <div style={{color:'#6b7280',fontSize:12,marginTop:6}}>Tip: separate ingredients with spaces or commas. Example: egg bread tomato onion</div>

        <div style={{ marginTop: 16, display: 'flex', gap: 8, alignItems: 'center' }}>
          <button
            className="btn btn-primary"
            onClick={handleGenerate}
            disabled={loading}
            style={{ padding: '10px 14px' }}
          >
            {loading? 'Generating…' : 'Generate'}
          </button>
          <span style={{color:'#64748b',fontSize:13}}>{status}</span>
        </div>

        {recipe && (
          <div style={{ marginTop: 18, borderTop: '1px solid #eee', paddingTop: 14 }}>
            <h4 style={{ margin: '0 0 6px' }}>{recipe.title} <span style={{ color: '#6b7280' }}>· {recipe.cuisine}</span></h4>
            <p style={{ marginTop: 0, color: '#475569' }}>{recipe.description}</p>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 14 }}>
              <div>
                <div style={{ fontWeight: 600, marginBottom: 6 }}>Ingredients</div>
                <ul style={{ margin: 0, paddingLeft: 18 }}>
                  {recipe.ingredients.map((it,i)=>(<li key={i}>{it}</li>))}
                </ul>
              </div>
              <div>
                <div style={{ fontWeight: 600, marginBottom: 6 }}>Steps</div>
                <ol style={{ margin: 0, paddingLeft: 18 }}>
                  {recipe.steps.map((s,i)=>(<li key={i} style={{marginBottom:6}}>{s}</li>))}
                </ol>
              </div>
            </div>
            <div style={{ marginTop: 10, color: '#64748b' }}>
              Ready in ~{recipe.time} mins · Serves {recipe.servings}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
