import React, { useEffect, useState } from 'react';
import api from '../services/api';
import './recipeList.css';
import RecipeCard from './RecipeCard';
import RecipeModal from './RecipeModal';
import ProfileDrawer from './ProfileDrawer';
import Toast from './Toast';
import AIGenerateModal from './AIGenerateModal';

export default function PublicFeed() {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [query, setQuery] = useState('');
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [drawerProfile, setDrawerProfile] = useState(null);
  const [modalRecipe, setModalRecipe] = useState(null);
  const [toast, setToast] = useState({ open: false, message: '', undoId: null });
  const [offline, setOffline] = useState(false);
  const [aiOpen, setAiOpen] = useState(false);
  const [trending, setTrending] = useState([]);

  const demoRecipes = [
    { _id: 'demo1', title: 'Herb-Crusted Grilled Salmon', description: 'Demo card shown while API is offline.', imageUrl: '', calories: 380 },
    { _id: 'demo2', title: 'Fresh Garden Salad', description: 'Try starting the backend to see real recipes.', imageUrl: '', calories: 220 }
  ];

  const extractTags = (list=[]) => {
    const tagFreq = {};
    list.forEach(r => {
      const base = (r.title + ' ' + (r.description||'') + ' ' + (r.taste||'')).toLowerCase();
      ['spicy','tangy','herby','creamy','smoky','fresh','sweet','umami'].forEach(t => { if (base.includes(t)) tagFreq[t] = (tagFreq[t]||0)+1; });
    });
    return Object.entries(tagFreq).sort((a,b)=>b[1]-a[1]).slice(0,6).map(x=>x[0]);
  };

  const fetchFeed = async (q) => {
    setLoading(true); setError(null); setOffline(false);
    try {
      const url = q ? `/recipes/search?q=${encodeURIComponent(q)}` : '/recipes/feed';
      const res = await api.get(url);
      const data = Array.isArray(res.data) ? res.data : [];
      setRecipes(data);
      setTrending(extractTags(data));
    } catch (err) {
      if (!err.response) {
        setOffline(true);
        setError('Backend unreachable (start server on PORT 5000)');
        setRecipes(demoRecipes);
        setTrending(extractTags(demoRecipes));
      } else {
        setError(err.response?.data?.message || err.message || 'Failed to load feed');
        setRecipes([]);
        setTrending([]);
      }
    } finally { setLoading(false); }
  };

  useEffect(() => { fetchFeed(''); }, []);

  const isCiTest = (r) => {
    if (!r || !r.title) return false;
    const t = (r.title || '').toLowerCase();
    if (t.includes('ci test') || t.includes('ci-test') || t.includes('ci_test')) return true;
    if (t === 'test' || t === 'test recipe' || t.includes('test recipe')) return true;
    if (t === 'ci' || t === 'test-only' || t.includes('ci-only')) return true;
    return false;
  };

  const onSearch = (e) => { e.preventDefault(); fetchFeed(query); };
  const openRecipeModal = (r) => setModalRecipe(r);
  const closeModal = () => setModalRecipe(null);

  const handleDelete = async (id) => {
    try { await api.delete(`/recipes/${id}`); setToast({ open: true, message: 'Recipe deleted', undoId: id }); fetchFeed(query); }
    catch { alert('Delete failed'); }
  };
  const undoDelete = async () => {
    try { if (!toast.undoId) return; await api.post(`/recipes/${toast.undoId}/restore`); setToast({ open: false, message: '', undoId: null }); fetchFeed(query); } catch {}
  };

  const applyTag = (t) => { setQuery(t); fetchFeed(t); };

  const visible = recipes.filter(r => !isCiTest(r));

  return (
    <div className="rl-root">
      {/* Header with count */}
      <div className="feed-header">
        <h2><span className="feed-icon" aria-hidden>üçΩ</span> Public Feed</h2>
        <form className="feed-search" onSubmit={onSearch}>
          <input value={query} onChange={e => setQuery(e.target.value)} placeholder="Search recipes or users" />
          <button type="submit" className="search-btn">Search</button>
          <button type="button" className="clear-btn" onClick={() => { setQuery(''); fetchFeed(''); }}>Clear</button>
        </form>
        <div className="feed-meta" style={{ marginTop:8, fontSize:13, color:'#475569' }}>Showing {recipes.length} recipe{recipes.length!==1?'s':''}</div>
        {trending.length > 0 && (
          <div className="trend-chips" style={{ display:'flex', flexWrap:'wrap', gap:6, marginTop:10 }}>
            {trending.map(t => (
              <button key={t} type="button" onClick={()=>applyTag(t)} style={{ background:'#f1f5f9', border:'1px solid #e2e8f0', padding:'4px 10px', borderRadius:20, fontSize:12, cursor:'pointer' }}>#{t}</button>
            ))}
          </div>
        )}
      </div>

      {error && <div className="feed-error"><div>{error}</div><button onClick={() => fetchFeed(query)}>Retry</button></div>}

      {offline && (
        <div className="empty" style={{ background:'#fffbf6', border:'1px solid #ffe7d7', borderRadius:10, padding:14 }}>Backend offline: start it via ‚Äúcd server && npm run dev‚Äù. Showing demo recipes.</div>
      )}

      {loading ? (
        <div className="rl-grid wide">
          {Array.from({length:6}).map((_,i)=>(
            <div key={i} className="skeleton-card" style={{ background:'#f1f5f9', height:220, borderRadius:12, animation:'pulse 1.4s ease-in-out infinite' }} />
          ))}
        </div>
      ) : (
        <div className="rl-grid wide">
          {visible.length === 0 ? <div className="empty">No recipes found.</div> : visible.map(r => (
            <div key={r._id || r.id}><RecipeCard recipe={r} onChange={() => fetchFeed(query)} onDelete={handleDelete} onOpen={openRecipeModal} /></div>
          ))}
        </div>
      )}

      {/* Modal & drawers */}
      <RecipeModal open={!!modalRecipe} recipe={modalRecipe} onClose={closeModal} />
      <ProfileDrawer open={drawerOpen} profile={drawerProfile} onClose={() => setDrawerOpen(false)} />
      <Toast open={toast.open} message={toast.message} actionText="Undo" onAction={undoDelete} onClose={() => setToast({ open: false })} />

      {/* Floating AI button */}
      <button className="ai-fab" title="Generate a recipe from your ingredients" onClick={() => setAiOpen(true)}>
        <span className="ai-fab-icon">‚ú®</span>
        <span className="ai-fab-label">AI Recipe</span>
      </button>
      <AIGenerateModal open={aiOpen} onClose={() => setAiOpen(false)} />
    </div>
  );
}
