import React, { useMemo, useState } from 'react';
import api from '../services/api';

function tokenize(list) {
  return list
    .split(/[\s,;|\n/]+/g) // split on spaces, commas, semicolons, pipes, newlines, slashes
    .map(s => s.trim().toLowerCase())
    .filter(Boolean);
}

function titleCase(s) { return s.replace(/\b\w/g, c => c.toUpperCase()); }

function localGenerate(ingList) {
  const raw = tokenize(ingList);
  if (!raw.length) return null;
  const set = new Set(raw);
  const hasAny = (...words) => words.some(w => set.has(w));

  // Synonyms/groups
  const BEANS = ['beans','green beans','french beans','kidney beans','rajma','lobia','bean'];
  const POTATO = ['potato','potatoes','aloo'];
  const ONION = ['onion','onions'];
  const TOMATO = ['tomato','tomatoes'];
  const GARLIC = ['garlic'];
  const GINGER = ['ginger'];
  const CHILI = ['chili','chilli','green chili','green chilli'];
  const PEAS = ['peas','matar'];
  const PANEER = ['paneer'];
  const CHICKPEA = ['chickpea','chickpeas','chana','chole','gram'];
  const LENTIL = ['lentil','lentils','dal','daal','dahl','masoor','moong','toor','arhar'];
  const RICE = ['rice'];
  const EGG = ['egg','eggs'];
  const BREAD = ['bread'];
  const PASTA = ['pasta','noodles','spaghetti','macaroni'];
  const CHEESE = ['cheese'];
  const CAPSICUM = ['capsicum','bell pepper','pepper'];
  const CHICKEN = ['chicken'];

  const hasGroup = (group) => group.some(g => set.has(g));

  // Decide dish by common combos
  let title = 'Chef’s Quick Creation';
  let cuisine = 'Fusion';
  let description = 'A quick dish from your ingredients.';
  let style = 'generic'; // generic | indian | stirfry | pasta

  if (hasGroup(BEANS) && hasGroup(POTATO)) {
    title = 'Aloo Beans Sabzi'; cuisine = 'Indian'; style = 'indian';
    description = 'A dry-style Indian sabzi of beans and potatoes with simple spices.';
  } else if (hasGroup(CHICKPEA) && (hasGroup(ONION) || hasGroup(TOMATO))) {
    title = 'Quick Chana Masala'; cuisine = 'Indian'; style = 'indian';
    description = 'A quick, comforting chickpea curry with onion-tomato base.';
  } else if (hasGroup(LENTIL) && (hasGroup(ONION) || hasGroup(TOMATO))) {
    title = 'Masoor Dal Tadka'; cuisine = 'Indian'; style = 'indian';
    description = 'Simple red lentil dal finished with a fragrant tadka.';
  } else if (hasGroup(RICE) && (hasGroup(PEAS) || hasGroup(POTATO))) {
    title = 'Veg Pulao'; cuisine = 'Indian'; style = 'indian';
    description = 'One-pot aromatic rice with veggies and mild spices.';
  } else if (hasGroup(EGG) && hasGroup(POTATO)) {
    title = 'Spanish-Style Potato Omelette'; cuisine = 'Mediterranean'; style = 'stirfry';
    description = 'A hearty skillet omelette of potatoes and eggs.';
  } else if (hasGroup(PANEER) && (hasGroup(TOMATO) || hasGroup(ONION) || hasGroup(CAPSICUM))) {
    title = 'Paneer Jalfrezi'; cuisine = 'Indian'; style = 'indian';
    description = 'Stir-fried paneer with peppers in a tangy masala.';
  } else if (hasGroup(PASTA) && (hasGroup(TOMATO) || hasGroup(GARLIC))) {
    title = 'Tomato Garlic Pasta'; cuisine = 'Italian'; style = 'pasta';
    description = 'Al dente pasta tossed in a garlicky tomato sauce.';
  } else if (hasGroup(BREAD) && (hasGroup(TOMATO) || hasGroup(CHEESE) || hasGroup(ONION))) {
    title = 'Masala Cheese Toast'; cuisine = 'Café'; style = 'stirfry';
    description = 'Toasted bread with a masala topping and melted cheese.';
  } else if (hasGroup(CHICKEN) && (hasGroup(GARLIC) || hasGroup(ONION))) {
    title = 'Garlic Chicken Stir-Fry'; cuisine = 'Asian'; style = 'stirfry';
    description = 'Tender chicken tossed quickly with aromatics and sauce.';
  } else if (hasGroup(BEANS)) {
    title = 'Garlicky Stir-Fried Beans'; cuisine = 'Asian'; style = 'stirfry';
    description = 'Crisp-tender beans with garlic and soy notes.';
  } else if (hasGroup(POTATO)) {
    title = 'Crispy Masala Potatoes'; cuisine = 'Indian'; style = 'indian';
    description = 'Golden pan-fried potatoes with warm spices.';
  }

  // Compose ingredients: use provided + minimal pantry/spices based on style
  const baseIngredients = Array.from(new Set(raw));
  const pantry = ['salt','black pepper','oil'];
  const indianSpices = ['turmeric','coriander powder','red chilli','cumin seeds'];
  const sauceBits = ['soy sauce','vinegar'];
  let extra = [];
  if (style === 'indian') { extra = indianSpices; }
  else if (style === 'stirfry') { extra = sauceBits; }

  const ingredients = Array.from(new Set([...baseIngredients, ...pantry, ...extra])).map(titleCase);

  // Steps vary a bit by style
  let steps;
  if (style === 'indian') {
    steps = [
      'Prep: wash, peel and chop vegetables.',
      'Heat oil in a pan; add cumin seeds and let them splutter.',
      'Add onion (and ginger-garlic if available); sauté till translucent.',
      'Stir in turmeric, coriander powder and red chilli; cook briefly.',
      'Add main ingredients and a splash of water; cook covered until tender.',
      'Season with salt; finish with lemon/coriander and serve.'
    ];
  } else if (style === 'pasta') {
    steps = [
      'Boil pasta in salted water until al dente; reserve a little pasta water.',
      'Sauté garlic (and chili flakes if using) in olive oil.',
      'Add tomatoes; cook down into a sauce, season with salt and pepper.',
      'Toss pasta with the sauce; add pasta water to loosen and emulsify.',
      'Finish with herbs/cheese and serve hot.'
    ];
  } else {
    steps = [
      'Prep ingredients (wash, chop as needed).',
      'Heat a pan with a little oil on medium-high.',
      'Add aromatics first (like onion/garlic) and sauté till fragrant.',
      'Add main ingredients and cook until done, stirring occasionally.',
      'Season to taste and finish with herbs/chili/lemon.'
    ];
  }

  const time = Math.min(40, 12 + baseIngredients.length * 3);
  const servings = baseIngredients.length >= 6 ? 3 : 2;

  return { title, cuisine, description, ingredients, steps, time, servings };
}

export default function AIGenerateModal({ open, onClose }) {
  const [raw, setRaw] = useState('egg, tomato, onion, bread');
  const [remote, setRemote] = useState(null);
  const [loading, setLoading] = useState(false);
  const [status, setStatus] = useState('');
  const localRecipe = useMemo(() => localGenerate(raw), [raw]);
  const recipe = remote || localRecipe;

  if (!open) return null;

  const handleGenerate = async () => {
    setLoading(true);
    setRemote(null);
    setStatus('');
    try {
      const res = await api.post('/ai/generate', { ingredients: raw });
      setRemote(res.data);
      setStatus('AI result');
    } catch (e) {
      console.warn('AI endpoint unavailable, using local generator');
      setStatus('Local suggestion');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      role="dialog"
      aria-modal="true"
      onClick={(e)=>{ if(e.target===e.currentTarget) onClose?.(); }}
      style={{
        position: 'fixed', inset: 0, background: 'rgba(0,0,0,0.45)',
        zIndex: 1000, display: 'flex', alignItems: 'center', justifyContent: 'center'
      }}
    >
      <div style={{
        width: 'min(720px, 92vw)', maxHeight: '88vh', overflow: 'auto',
        background: '#fff', borderRadius: 12, boxShadow: '0 12px 40px rgba(0,0,0,0.25)',
        padding: 20
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', gap: 12 }}>
          <h3 style={{ margin: 0 }}>Generate Recipe from Ingredients</h3>
          <button onClick={onClose} className="btn" style={{ padding: '6px 10px' }}>✕</button>
        </div>

        <label style={{ display: 'block', marginTop: 12, fontWeight: 600 }}>Your ingredients (comma or new line):</label>
        <textarea
          value={raw}
          onChange={e => setRaw(e.target.value)}
          rows={4}
          placeholder="e.g. egg bread tomato onion OR egg, bread, tomato, onion"
          style={{
            width: '100%', borderRadius: 8, border: '1px solid #d4d7dd',
            padding: 10, fontSize: 14, marginTop: 6
          }}
        />
        <div style={{color:'#6b7280',fontSize:12,marginTop:6}}>Tip: separate ingredients with spaces or commas. Example: egg bread tomato onion</div>

        <div style={{ marginTop: 16, display: 'flex', gap: 8, alignItems: 'center' }}>
          <button
            className="btn btn-primary"
            onClick={handleGenerate}
            disabled={loading}
            style={{ padding: '10px 14px' }}
          >
            {loading? 'Generating…' : 'Generate'}
          </button>
          <span style={{color:'#64748b',fontSize:13}}>{status}</span>
        </div>

        {recipe && (
          <div style={{ marginTop: 18, borderTop: '1px solid #eee', paddingTop: 14 }}>
            <h4 style={{ margin: '0 0 6px' }}>{recipe.title} <span style={{ color: '#6b7280' }}>· {recipe.cuisine}</span></h4>
            <p style={{ marginTop: 0, color: '#475569' }}>{recipe.description}</p>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 14 }}>
              <div>
                <div style={{ fontWeight: 600, marginBottom: 6 }}>Ingredients</div>
                <ul style={{ margin: 0, paddingLeft: 18 }}>
                  {recipe.ingredients.map((it,i)=>(<li key={i}>{it}</li>))}
                </ul>
              </div>
              <div>
                <div style={{ fontWeight: 600, marginBottom: 6 }}>Steps</div>
                <ol style={{ margin: 0, paddingLeft: 18 }}>
                  {recipe.steps.map((s,i)=>(<li key={i} style={{marginBottom:6}}>{s}</li>))}
                </ol>
              </div>
            </div>
            <div style={{ marginTop: 10, color: '#64748b' }}>
              Ready in ~{recipe.time} mins · Serves {recipe.servings}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
