import React from 'react';
import { Link } from 'react-router-dom';
import api from '../services/api';

export default function RecipeCard({ recipe, onChange }) {
  const toggleFav = async () => {
    try {
      await api.post(`/recipes/${recipe._id}/favorite`);
      onChange();
    } catch (err) { console.error(err); }
  };

  const base = process.env.REACT_APP_API_URL?.replace('/api','') || 'http://localhost:5000';

  const openProfile = (id) => {
    // dispatch a custom event so feed/listening components can open the drawer
    window.dispatchEvent(new CustomEvent('openProfileDrawer', { detail: { id } }));
  };

  return (
    <div className="recipe-card card">
      {recipe.imageUrl && <img src={`${base}${recipe.imageUrl}`} alt="" />}
      <div className="meta">
        <Link to={`/recipe/${recipe._id}`} className="title">{recipe.title}</Link>
        {recipe.owner && <div style={{ fontSize:12, color:'#64748b' }}>By <a href={`/user/${recipe.owner._id || recipe.owner}`} onClick={e => { e.preventDefault(); openProfile(recipe.owner._id || recipe.owner); }}>{recipe.owner.name || (typeof recipe.owner === 'string' ? recipe.owner : 'Chef')}</a></div>}
        <div className="cal">{recipe.caloriesTotal} kcal</div>
      </div>
      <div className="actions">
        <button onClick={toggleFav}>â™¥</button>
      </div>
    </div>
  );
}
