import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import heroImg from '../assets/hero.jpg';
import salmon from '../assets/salmon.jpg';
import carbonara from '../assets/carbonara.jpg';

// Floating image animation config
const floatTransition = {
  repeat: Infinity,
  repeatType: 'mirror',
  duration: 8,
  ease: 'easeInOut'
};

export default function Landing() {
  return (
    <div className="relative min-h-screen w-full bg-slate-50 font-sans text-gray-800 overflow-hidden">
      {/* Aurora / Glow Background */}
      <div className="pointer-events-none absolute inset-0 -z-10">
        <div className="absolute top-10 left-8 w-[420px] h-[420px] rounded-full bg-orange-300 opacity-30 blur-[120px]" />
        <div className="absolute bottom-0 right-10 w-[480px] h-[480px] rounded-full bg-blue-200 opacity-30 blur-[120px]" />
        <div className="absolute top-1/3 right-1/3 w-[360px] h-[360px] rounded-full bg-orange-200 opacity-25 blur-[110px]" />
      </div>

      {/* HERO SECTION */}
      <section className="relative max-w-7xl mx-auto px-6 md:px-10 pt-24 pb-28 flex flex-col items-center text-center">
        <motion.h1
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="font-playfair font-black tracking-tight leading-[1.05] text-6xl md:text-8xl text-gray-900 drop-shadow-sm"
        >
          The Future of Flavor
        </motion.h1>
        <motion.p
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3, duration: 0.7 }}
          className="mt-8 max-w-2xl text-lg md:text-xl text-gray-600"
        >
          HomePlate is your intelligent cooking workspace: organize, generate, and evolve recipes powered by AI & nutrition insights.
        </motion.p>
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.5, duration: 0.6 }}
          className="mt-10 flex flex-wrap gap-5 justify-center"
        >
          <Link
            to="/signup"
            className="px-8 py-4 rounded-full bg-gradient-to-r from-orange-500 to-amber-500 text-white font-semibold shadow-lg shadow-orange-400/30 hover:shadow-xl hover:-translate-y-0.5 transition-transform"
          >
            Get Started Free
          </Link>
          <Link
            to="/signin"
            className="px-8 py-4 rounded-full border border-orange-300 text-orange-600 font-medium bg-white/70 backdrop-blur hover:bg-white transition"
          >
            Sign In
          </Link>
        </motion.div>

        {/* Floating Images */}
        <div className="relative w-full max-w-5xl mt-24 h-[360px]">
          <motion.div
            animate={{ y: [0, -20, 0] }}
            transition={floatTransition}
            className="absolute left-4 top-10 w-56 h-72 rounded-3xl overflow-hidden shadow-xl shadow-orange-200/50 border border-white/60 bg-white/40 backdrop-blur"
          >
            <img src={heroImg} alt="Signature Dish" className="w-full h-full object-cover" />
          </motion.div>
          <motion.div
            animate={{ y: [0, -20, 0] }}
            transition={{ ...floatTransition, duration: 7 }}
            className="absolute right-6 top-4 w-64 h-80 rounded-3xl overflow-hidden shadow-xl shadow-blue-200/50 border border-white/60 bg-white/40 backdrop-blur"
          >
            <img src={salmon} alt="Fresh Salmon" className="w-full h-full object-cover" />
          </motion.div>
          <motion.div
            animate={{ y: [0, -25, 0] }}
            transition={{ ...floatTransition, duration: 6 }}
            className="absolute left-1/2 -translate-x-1/2 bottom-0 w-48 h-64 rounded-3xl overflow-hidden shadow-xl shadow-amber-200/50 border border-white/60 bg-white/40 backdrop-blur"
          >
            <img src={carbonara} alt="Classic Carbonara" className="w-full h-full object-cover" />
          </motion.div>
        </div>
      </section>

      {/* MARQUEE STRIP */}
      <section className="relative w-full py-6 bg-gray-900 -rotate-1 shadow-lg">
        <div className="overflow-hidden">
          <motion.div
            className="flex text-white text-lg font-medium whitespace-nowrap"
            animate={{ x: [0, -1000] }}
            transition={{ repeat: Infinity, duration: 18, ease: 'linear' }}
          >
            {Array.from({ length: 8 }).map((_, i) => (
              <span key={i} className="mx-8">Fresh • Organic • AI-Powered • Tasty • Crafted • Insight Driven •</span>
            ))}
          </motion.div>
        </div>
      </section>

      {/* FEATURES BENTO GRID */}
      <section className="max-w-7xl mx-auto px-6 md:px-10 py-24">
        <motion.h2
          initial={{ opacity: 0, y: 40 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.7 }}
          className="font-playfair font-black text-4xl md:text-6xl text-center mb-16"
        >
          Powering Your Culinary Flow
        </motion.h2>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 auto-rows-[minmax(220px,_1fr)]">
          {/* Wide Card */}
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.6 }}
            className="md:col-span-2 rounded-3xl p-8 bg-white border border-gray-200 shadow-xl flex flex-col justify-between"
          >
            <div>
              <h3 className="text-2xl font-bold mb-3">AI Recipe Generation</h3>
              <p className="text-sm text-gray-600 leading-relaxed">Drop ingredients and instantly receive chef-quality suggestions with computed nutrition and health flags.</p>
            </div>
            <div className="mt-6 flex flex-wrap gap-2 text-xs">
              {['Speed','Zero Waste','Nutrition','Customization'].map(t => (
                <span key={t} className="px-3 py-1 rounded-full bg-gradient-to-r from-orange-500 to-amber-500 text-white shadow">{t}</span>
              ))}
            </div>
          </motion.div>

          {/* Dark Tall Card */}
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
