import React, { useState } from 'react';
import api from '../services/api';

export default function AIGenerateModal({ open, onClose }) {
  const [ingredients, setIngredients] = useState('');
  const [prompt, setPrompt] = useState('');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);

  // Quick Add ingredient suggestions
  const quickAddIngredients = [
    'Chicken', 'Rice', 'Pasta', 'Egg', 
    'Tomato', 'Cheese', 'Potato', 'Onion',
    'Garlic', 'Bell Pepper', 'Spinach', 'Mushroom'
  ];

  const handleQuickAdd = (ingredient) => {
    const currentIngredients = ingredients.trim();
    if (currentIngredients) {
      setIngredients(currentIngredients + ', ' + ingredient);
    } else {
      setIngredients(ingredient);
    }
  };

  const handleGenerate = async () => {
    if (!ingredients.trim()) return;

    setLoading(true);
    try {
      const response = await api.post('/recipes/generate-ai', {
        ingredients: ingredients.split(',').map(i => i.trim()),
        prompt: prompt.trim()
      });
      setResult(response.data);
    } catch (error) {
      console.error('AI generation error:', error);
      alert('Failed to generate recipe. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  if (!open) return null;

  return (
    <div 
      style={{ 
        position: 'fixed',
        inset: 0,
        background: 'rgba(0, 0, 0, 0.6)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        zIndex: 9999,
        overflowY: 'auto',
        overflowX: 'hidden',
        padding: '20px'
      }}
      onClick={onClose}
    >
      <div 
        onClick={(e) => e.stopPropagation()}
        style={{
          background: 'white',
          borderRadius: '16px',
          boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25)',
          maxWidth: '600px',
          width: '100%',
          maxHeight: '90vh',
          overflowY: 'auto',
          overflowX: 'hidden',
          position: 'relative'
        }}
      >
        {/* Close Button */}
        <button 
          onClick={onClose}
          style={{
            position: 'absolute',
            top: '16px',
            right: '16px',
            width: '32px',
            height: '32px',
            background: '#F3F4F6',
            border: 'none',
            borderRadius: '50%',
            cursor: 'pointer',
            fontSize: '18px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            transition: 'all 0.2s ease',
            zIndex: 10
          }}
          onMouseEnter={(e) => e.currentTarget.style.background = '#E5E7EB'}
          onMouseLeave={(e) => e.currentTarget.style.background = '#F3F4F6'}
        >
          ‚úï
        </button>

        <div style={{ padding: '32px' }}>
          {/* Header */}
          <h2 style={{ 
            fontFamily: "'Playfair Display', serif",
            fontSize: '32px',
            fontWeight: '700',
            marginBottom: '8px',
            color: '#1F2937'
          }}>
            ü§ñ AI Recipe Generator
          </h2>
          <p style={{ 
            color: '#6B7280',
            fontSize: '16px',
            marginBottom: '24px'
          }}>
            Enter ingredients and let AI create a recipe for you!
          </p>

          {loading ? (
            /* Loading State */
            <div style={{
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              padding: '60px 20px',
              gap: '20px'
            }}>
              {/* Spinner */}
              <div style={{
                width: '60px',
                height: '60px',
                border: '4px solid #F3F4F6',
                borderTop: '4px solid #E85D04',
                borderRadius: '50%',
                animation: 'spin 1s linear infinite'
              }} />

              <div style={{ textAlign: 'center' }}>
                <h3 style={{ 
                  fontSize: '24px',
                  fontWeight: '600',
                  color: '#1F2937',
                  marginBottom: '8px'
                }}>
                  üë®‚Äçüç≥ Chef Gemini is thinking...
                </h3>
                <p style={{ color: '#6B7280', fontSize: '14px' }}>
                  Creating the perfect recipe for you
                </p>
              </div>
            </div>
          ) : result ? (
            /* Result Display */
            <div>
              <h3 style={{ fontSize: '20px', fontWeight: '600', marginBottom: '12px' }}>
                Generated Recipe:
              </h3>
              <pre style={{ 
                background: '#F9FAFB', 
                padding: '16px', 
                borderRadius: '8px',
                whiteSpace: 'pre-wrap',
                fontSize: '14px'
              }}>
                {JSON.stringify(result, null, 2)}
              </pre>
              <button 
                onClick={() => { setResult(null); setIngredients(''); setPrompt(''); }}
                style={{
                  marginTop: '16px',
                  padding: '12px 24px',
                  background: '#E85D04',
                  color: 'white',
                  border: 'none',
                  borderRadius: '8px',
                  cursor: 'pointer'
                }}
              >
                Generate Another
              </button>
            </div>
          ) : (
            /* Input Form */
            <div>
              {/* Ingredients Input */}
              <label style={{
                display: 'block',
                fontSize: '14px',
                fontWeight: '600',
                color: '#374151',
                marginBottom: '8px'
              }}>
                Ingredients (comma-separated):
              </label>
              <textarea
                value={ingredients}
                onChange={(e) => setIngredients(e.target.value)}
                placeholder="e.g., chicken, rice, tomatoes"
                rows="3"
                style={{
                  width: '100%',
                  padding: '12px',
                  background: '#F9FAFB',
                  border: '2px solid #E5E7EB',
                  borderRadius: '8px',
                  fontSize: '15px',
                  resize: 'vertical',
                  transition: 'all 0.2s ease',
                  fontFamily: 'inherit',
                  boxSizing: 'border-box'
                }}
                onFocus={(e) => {
                  e.target.style.borderColor = '#E85D04';
                  e.target.style.background = '#ffffff';
                }}
                onBlur={(e) => {
                  e.target.style.borderColor = '#E5E7EB';
                  e.target.style.background = '#F9FAFB';
                }}
              />

              {/* Quick Add Chips */}
              <div style={{ marginTop: '12px', marginBottom: '20px' }}>
                <p style={{
                  fontSize: '13px',
                  fontWeight: '600',
                  color: '#6B7280',
                  marginBottom: '8px'
                }}>
                  Quick Add:
                </p>
                <div style={{
                  display: 'flex',
                  flexWrap: 'wrap',
                  gap: '8px'
                }}>
                  {quickAddIngredients.map((ingredient) => (
                    <button
                      key={ingredient}
                      type="button"
                      onClick={() => handleQuickAdd(ingredient)}
                      style={{
                        padding: '6px 12px',
                        background: '#F3F4F6',
                        border: '1px solid #E5E7EB',
                        borderRadius: '20px',
                        fontSize: '13px',
                        fontWeight: '500',
                        color: '#374151',
                        cursor: 'pointer',
                        transition: 'all 0.2s ease'
                      }}
                      onMouseEnter={(e) => {
                        e.currentTarget.style.background = '#E85D04';
                        e.currentTarget.style.color = 'white';
                        e.currentTarget.style.borderColor = '#E85D04';
                      }}
                      onMouseLeave={(e) => {
                        e.currentTarget.style.background = '#F3F4F6';
                        e.currentTarget.style.color = '#374151';
                        e.currentTarget.style.borderColor = '#E5E7EB';
                      }}
                    >
                      + {ingredient}
                    </button>
                  ))}
                </div>
              </div>

              {/* Optional Prompt */}
              <label style={{
                display: 'block',
                fontSize: '14px',
                fontWeight: '600',
                color: '#374151',
                marginBottom: '8px'
              }}>
                Additional Prompt (optional):
              </label>
              <textarea
                value={prompt}
                onChange={(e) => setPrompt(e.target.value)}
                placeholder="e.g., make it spicy, low-carb, Italian-style"
                rows="2"
                style={{
                  width: '100%',
                  padding: '12px',
                  background: '#F9FAFB',
                  border: '2px solid #E5E7EB',
                  borderRadius: '8px',
                  fontSize: '15px',
                  resize: 'vertical',
                  marginBottom: '20px',
                  transition: 'all 0.2s ease',
                  fontFamily: 'inherit',
                  boxSizing: 'border-box'
                }}
                onFocus={(e) => {
                  e.target.style.borderColor = '#E85D04';
                  e.target.style.background = '#ffffff';
                }}
                onBlur={(e) => {
                  e.target.style.borderColor = '#E5E7EB';
                  e.target.style.background = '#F9FAFB';
                }}
              />

              {/* Generate Button */}
              <button 
                onClick={handleGenerate} 
                disabled={!ingredients.trim()}
                style={{
                  width: '100%',
                  padding: '14px',
                  background: ingredients.trim() ? 'linear-gradient(135deg, #E85D04, #F48C06)' : '#D1D5DB',
                  color: 'white',
                  border: 'none',
                  borderRadius: '8px',
                  fontSize: '16px',
                  fontWeight: '700',
                  cursor: ingredients.trim() ? 'pointer' : 'not-allowed',
                  transition: 'all 0.3s ease',
                  boxShadow: ingredients.trim() ? '0 4px 12px rgba(232, 93, 4, 0.3)' : 'none'
                }}
                onMouseEnter={(e) => {
                  if (ingredients.trim()) {
                    e.currentTarget.style.transform = 'translateY(-2px)';
                    e.currentTarget.style.boxShadow = '0 6px 20px rgba(232, 93, 4, 0.4)';
                  }
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = 'translateY(0)';
                  e.currentTarget.style.boxShadow = ingredients.trim() ? '0 4px 12px rgba(232, 93, 4, 0.3)' : 'none';
                }}
              >
                ‚ú® Generate Recipe
