import React from 'react';
import { Link } from 'react-router-dom';
import api from '../services/api';

export default function RecipeCard({ recipe, onChange }) {
  const toggleFav = async () => {
    try {
      await api.post(`/recipes/${recipe._id}/favorite`);
      onChange();
    } catch (err) { console.error(err); }
  };

  return (
    <div className="recipe-card card">
      {recipe.imageUrl && <img src={`http://localhost:5000${recipe.imageUrl}`} alt="" />}
      <div className="meta">
        <Link to={`/recipe/${recipe._id}`} className="title">{recipe.title}</Link>
        <div className="cal">{recipe.caloriesTotal} kcal</div>
      </div>
      <div className="actions">
        <button onClick={toggleFav}>â™¥</button>
      </div>
    </div>
  );
}
